package org.tnsif.looping;

public class EnhancedForLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c[] = {'a', 's'};
		for(int i:c)
		{
			System.out.println(i+ " ");
		}
	}
/*we get output as ASCII values of alphabets provided in Char array 
	and i is an integer and we are storing array elements into i that's why we get integer ASCII values in result.*/
}
